import React, { useState } from 'react';
import ClusterSelect from './components/ClusterSelect';
import TopicSelect from './components/TopicSelect';
import QuestionView from './components/QuestionView';
import { loadQuestions } from './utils';

import './index.css';

function App() {
  const data = loadQuestions();
  const [selectedCluster, setSelectedCluster] = useState(null);
  const [selectedTopic, setSelectedTopic] = useState(null);

  return (
    <div className="app">
      <div className="header">
        <h1>Question Practice App</h1>
        <div className="small">Local progress saved in browser</div>
      </div>

      {!selectedCluster && !selectedTopic && (
        <ClusterSelect clusters={data.clusters} onSelect={(c) => setSelectedCluster(c)} />
      )}

      {selectedCluster && !selectedTopic && (
        <TopicSelect cluster={selectedCluster} onBack={() => setSelectedCluster(null)} onSelect={(t) => setSelectedTopic(t)} />
      )}

      {selectedTopic && (
        <QuestionView topic={selectedTopic} onBack={() => setSelectedTopic(null)} />
      )}

    </div>
  );
}

export default App;
