import React from 'react';
import LatexText from './LatexText';
import { loadQuestions, loadProgress, findQuestionById } from '../utils';

export default function SavedProblems({ onOpenTopic }) {
  const data = loadQuestions();
  const progress = loadProgress();
  const savedIds = progress.savedQuestions || [];

  const savedQuestions = savedIds
    .map(id => findQuestionById(data, id))
    .filter(Boolean);

  return (
    <div>
      <div className="header">
        <h2>Saved Problems</h2>
      </div>

      {savedQuestions.length === 0 ? (
        <div style={{ marginTop: 12 }}>No saved problems.</div>
      ) : (
        <ul className="list">
          {savedQuestions.map((q) => (
            <li key={q.id}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div style={{ maxWidth: '70%' }}>
                  <div><strong>#{q.id}</strong></div>
                  <div className="small"><LatexText text={q.question} /></div>
                </div>
                <div>
                  <button className="btn" onClick={() => onOpenTopic(q.cluster, q.topic)}>Open Topic</button>
                </div>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
