import React, { useState } from 'react';
import ClusterSelect from './components/ClusterSelect';
import TopicSelect from './components/TopicSelect';
import QuestionView from './components/QuestionView';
import HomeView from './components/HomeView';
import SavedProblems from './components/SavedProblems';
import { loadQuestions } from './utils';

import './index.css';

function App() {
  const data = loadQuestions();
  const [selectedCluster, setSelectedCluster] = useState(null);
  const [selectedTopic, setSelectedTopic] = useState(null);
  const [view, setView] = useState('home'); // home | cluster | topic | saved

  return (
    <div className="app">
      <div className="header">
        <h1>Question Practice App</h1>
        <div className="small">Local progress saved in browser</div>
      </div>

      {view === 'home' && (
        <HomeView
          onOpenCluster={(cluster, topic) => {
            if (cluster && topic) {
              setSelectedCluster({ name: cluster.name || cluster });
              // find topic object
              const t = (data.clusters || []).find(c => c.name === (cluster.name || cluster));
            }
            if (topic) {
              // find the topic object inside clusters
              let foundTopic = null;
              for (const c of data.clusters || []) {
                const ft = (c.topics || []).find(tt => tt.name === topic.name || tt.name === topic);
                if (ft) { foundTopic = ft; break; }
              }
              if (foundTopic) {
                setSelectedTopic(foundTopic);
                setView('topic');
                return;
              }
            }
            // fallback: open cluster list
            setView('cluster');
          }}
          onOpenSaved={() => setView('saved')}
        />
      )}

      {view === 'cluster' && !selectedTopic && (
        <ClusterSelect clusters={data.clusters} onSelect={(c) => { setSelectedCluster(c); setView('cluster-topics'); }} />
      )}

      {view === 'cluster-topics' && selectedCluster && !selectedTopic && (
        <TopicSelect cluster={selectedCluster} onBack={() => { setSelectedCluster(null); setView('home'); }} onSelect={(t) => { setSelectedTopic(t); setView('topic'); }} />
      )}

      {view === 'topic' && selectedTopic && (
        <QuestionView topic={selectedTopic} onBack={() => { setSelectedTopic(null); setView('home'); }} />
      )}

      {view === 'saved' && (
        <div>
          <div className="header">
            <button className="btn" onClick={() => setView('home')}>Back</button>
          </div>
          <SavedProblems onOpenTopic={(clusterName, topicName) => {
            // find matching topic and open
            for (const c of data.clusters || []) {
              if (c.name === clusterName) {
                const ft = (c.topics || []).find(tt => tt.name === topicName);
                if (ft) {
                  setSelectedCluster(c);
                  setSelectedTopic(ft);
                  setView('topic');
                  return;
                }
              }
            }
          }} />
        </div>
      )}

    </div>
  );
}

export default App;
