import React from 'react';
import { loadQuestions, loadProgress, getProgressSummary } from '../utils';

export default function Sidebar({ onOpenCluster, onOpenSaved, onOpenHome }) {
  const data = loadQuestions();
  const progress = loadProgress();
  const summary = getProgressSummary(data, progress);

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h3>Topics</h3>
      </div>
      <div className="sidebar-content">
        <div className="sidebar-saved" style={{ display: 'flex', gap: 8 }}>
          <button className="btn" onClick={onOpenHome}>Home</button>
          <button className="btn" onClick={onOpenSaved}>Saved</button>
        </div>
        {summary.map((cluster, ci) => (
          <div key={ci} className="sidebar-section">
            <div className="sidebar-cluster">{cluster.name}</div>
            <ul className="sidebar-list">
              {cluster.topics.map((t, ti) => (
                <li key={ti} onClick={() => onOpenCluster(cluster.name, t.name)}>
                  <div className="sidebar-topic">
                    <span>{t.name}</span>
                    <span className="small">{t.completed}/{t.total}</span>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}
