import React, { useState, useEffect } from 'react';
import LatexText from './LatexText';
import { loadProgress, saveProgress, resetTopicProgress, getRandomQuestion, addSavedQuestion, removeSavedQuestion } from '../utils';

export default function QuestionView({ topic, onBack }) {
  const allQuestions = topic.questions || [];
  const [progress, setProgress] = useState(loadProgress());
  const [current, setCurrent] = useState(null);
  const [showHint, setShowHint] = useState(false);
  const [showAnswer, setShowAnswer] = useState(false);

  useEffect(() => {
    setProgress(loadProgress());
  }, []);

  useEffect(() => {
    // Pick a random question when the topic changes or when completedQuestions updates.
    const q = getRandomQuestion(allQuestions, progress.completedQuestions || []);
    setCurrent(q);
    setShowHint(false);
    setShowAnswer(false);
  }, [topic, progress.completedQuestions]);

  function pickRandom() {
    const q = getRandomQuestion(allQuestions, progress.completedQuestions || []);
    setCurrent(q);
    setShowHint(false);
    setShowAnswer(false);
  }

  function handleSave() {
    if (!current) return;
    // Toggle saved state
    const isSaved = (progress.savedQuestions || []).includes(current.id);
    const next = isSaved ? removeSavedQuestion(current.id) : addSavedQuestion(current.id);
    setProgress(next);
  }

  function handleMarkCompleted() {
    if (!current) return;
    const completed = new Set(progress.completedQuestions || []);
    completed.add(current.id);
    const next = { ...progress, completedQuestions: Array.from(completed) };
    setProgress(next);
    saveProgress(next);
  }

  function handleNext() {
    // If there are remaining questions, pick another. If none, current becomes null.
    const q = getRandomQuestion(allQuestions, progress.completedQuestions || []);
    setCurrent(q);
    setShowHint(false);
    setShowAnswer(false);
  }

  function handleReset() {
    const next = resetTopicProgress(topic);
    setProgress(next);
    setCurrent(getRandomQuestion(allQuestions, []));
    setShowHint(false);
    setShowAnswer(false);
  }

  const completedCount = (progress.completedQuestions || []).length;
  const totalCount = allQuestions.length;

  const [isExiting, setIsExiting] = useState(false);

  // Wrapper for next that triggers exit animation
  function handleNextWithAnimation() {
    setIsExiting(true);
    setTimeout(() => {
      handleNext();
      setIsExiting(false);
    }, 300); // Match animation duration
  }

  return (
    <div>
      <div className="header">
        <h2>{topic.name}</h2>
        <div>
          <button className="btn" onClick={onBack}>Back</button>
          <button className="btn" onClick={handleReset}>Reset Progress</button>
        </div>
      </div>

      <div className="footer">
        <div className="small">{completedCount}/{totalCount} completed</div>
        <div className="badge">Saved: {(progress.savedQuestions||[]).length}</div>
      </div>

      {current ? (
        <>
          <div className="flashcard-container">
            <div className={`flashcard ${isExiting ? 'exit' : ''}`}>
              <div className="flashcard-content">
                <LatexText text={current.question} />
              </div>
              
              {showHint && (
                <div style={{ marginTop: 20 }}>
                  <div><strong>Hint</strong></div>
                  <div className="small"><LatexText text={current.hint} /></div>
                </div>
              )}

              {showAnswer && (
                <div style={{ marginTop: 20 }}>
                  <div><strong>Answer</strong></div>
                  <div style={{ fontSize: '1.2rem', marginTop: 10 }}><LatexText text={current.answer} /></div>

                  {current.solution && (
                    <div style={{ marginTop: 16 }}>
                      <div><strong>Solution</strong></div>
                      <div className="small"><LatexText text={current.solution} /></div>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>

          <div className="controls-panel">
            <div className="controls">
              <button className="btn" onClick={() => setShowHint(!showHint)}>
                {showHint ? 'Hide Hint' : 'Show Hint'}
              </button>
              <button className="btn" onClick={() => setShowAnswer(!showAnswer)}>
                {showAnswer ? 'Hide Answer' : 'Show Answer & Solution'}
              </button>
              <button className="btn" onClick={handleSave}>
                {(progress.savedQuestions||[]).includes(current.id) ? 'Unsave' : 'Save for Later'}
              </button>
              <button className="btn btn-primary" onClick={handleMarkCompleted}>Mark Completed</button>
              <button className="btn" onClick={handleNextWithAnimation}>Next Question â†’</button>
            </div>
          </div>
        </>
      ) : (
        <div style={{ marginTop: 20 }}>
          <strong>All questions completed. Reset progress or select another topic.</strong>
        </div>
      )}
    </div>
  );
}
