import React from 'react';
import LatexText from './LatexText';
import { loadQuestions, loadProgress, findQuestionById, getProgressSummary } from '../utils';

export default function HomeView({ onOpenCluster, onOpenSaved }) {
  const data = loadQuestions();
  const progress = loadProgress();
  const summary = getProgressSummary(data, progress);

  return (
    <div>
      <div className="header">
        <h2>Home</h2>
        <div>
          <button className="btn" onClick={onOpenSaved}>Saved Problems</button>
        </div>
      </div>

      <div style={{ marginTop: 12 }}>
        <h3>Progress by Cluster & Topic</h3>
        {summary.map((cluster, ci) => (
          <div key={ci} style={{ marginBottom: 12 }}>
            <strong>{cluster.name}</strong>
            <ul className="list">
              {cluster.topics.map((t, ti) => (
                <li key={ti} style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <div onClick={() => onOpenCluster(cluster.name, t.name)} style={{ cursor: 'pointer' }}>
                    <div><strong>{t.name}</strong></div>
                    <div className="small">{t.completed}/{t.total} completed</div>
                  </div>
                  <div>
                    <button className="btn" onClick={() => onOpenCluster(cluster.name, t.name)}>Open</button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}
