import React from 'react';
import { loadQuestions, loadProgress, getProgressSummary } from '../utils';

export default function Sidebar({ onOpenCluster, onOpenSaved, onOpenHome, selectedCluster, selectedTopic }) {
  const data = loadQuestions();
    const summary = getProgressSummary(data, progress || { completedQuestions: [], savedQuestions: [] });
    const progress = loadProgress();

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h3>Topics</h3>
      </div>
      <div className="sidebar-content">
        <div className="sidebar-saved" style={{ display: 'flex', gap: 8 }}>
          <button className="btn" onClick={onOpenHome}>Home</button>
          <button className="btn" onClick={onOpenSaved}>Saved</button>
        </div>
        {summary.map((cluster, ci) => {
          const clusterActive = selectedCluster === cluster.name;
          const clusterPercentFloat = cluster.total ? ((cluster.completed / cluster.total) * 100).toFixed(1) : '0.0';
          return (
            <div key={ci} className={`sidebar-section ${clusterActive ? 'cluster-active' : ''}`}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div className="sidebar-cluster">{cluster.name}</div>
                <div style={{ textAlign: 'right' }} className="small" title={`${cluster.completed}/${cluster.total} completed`}>
                  {clusterPercentFloat}%
                </div>
              </div>
              <div style={{ marginTop: 6 }}>
                <div className="progress" aria-hidden>
                  <div className="progress-inner" style={{ width: `${cluster.percent}%` }} />
                </div>
              </div>
              <ul className="sidebar-list">
                {cluster.topics.map((t, ti) => {
                  const topicActive = clusterActive && selectedTopic === t.name;
                  const topicPercentFloat = t.total ? ((t.completed / t.total) * 100).toFixed(1) : '0.0';
                  return (
                    <li key={ti} className={`sidebar-item ${topicActive ? 'active' : ''}`} onClick={() => onOpenCluster(cluster.name, t.name)} title={`${t.completed}/${t.total} completed — ${topicPercentFloat}%`}>
                      <div className="sidebar-topic">
                        <span>{t.name}</span>
                        <span className="small">{topicPercentFloat}%</span>
                      </div>
                    </li>
                  );
                })}
              </ul>
            </div>
          );
        })}
      </div>
    </div>
  );
}
